mapper.select:
SELECT id, position_no, position_name, publish_time, position.server_create_date AS server_create_date, postion_status, resume_count FROM company_position_infos position JOIN (SELECT job_code, count(1) AS resume_count FROM job_seeker_resume_delivery GROUP BY job_code) AS t2 ON position_no = t2.job_code
AND position.company_infos_id = #{params.companyInfosId}
AND position.postion_status = #{params.postionStatus}
AND position.publish_time BETWEEN timestamp(DATE (#{params.publishTime,jdbcType=TIMESTAMP})) AND timestamp(DATE (#{params.publishTime,jdbcType=TIMESTAMP}),'23:59:59')
AND position.position_name like CONCAT('%',#{params.positionName},'%')

SELECT position.id AS id, company_name, company_infos_id, position_name, position_no, postion_status, remark, position.server_create_date as server_create_date, publish_time FROM company_position_infos position JOIN company_infos company ON position.company_infos_id=company.id
AND company.company_name = #{params.companyName}
AND position.postion_status = #{params.postionStatus}
AND position.position_name like CONCAT('%',#{params.positionName},'%')
AND position.server_create_date BETWEEN timestamp(DATE (#{params.serverCreateDate,jdbcType=TIMESTAMP})) AND timestamp(DATE (#{params.serverCreateDate,jdbcType=TIMESTAMP}),'23:59:59')
AND position.publish_time BETWEEN timestamp(DATE (#{params.publishTime})) AND timestamp(DATE (#{params.publishTime}),'23:59:59')

mapper.update:
update company_position_infos
set
postion_status = 3,
remark=#{message,jdbcType=VARCHAR}
where id = #{positionId,jdbcType=BIGINT}
